class RideTests(TestCase):
    def setUp(self):
        self.client = APIClient()
        self.client.credentials(HTTP_AUTHORIZATION=AUTH_TOKEN)

    def test_get_not_rated_rides_user_not_found(self):
        user = UserFactory(email='3monika100@gmail.com')
        ride = RideFactory(driver=user)
        response = self.client.get(f"/rides/100")
        results = json.loads(response.content)

        self.assertEqual(response.status_code, status.HTTP_404_NOT_FOUND)
        self.assertEqual(results, "User not found")

    def test_get_not_rated_ride_successful(self):
        user = UserFactory(email='3monika100@gmail.com')
        user2 = UserFactory()
        ride = RideFactory(driver=user, passengers=User.objects.filter(user_id=user2.user_id))
        response = self.client.get(f"/rides/{user2.user_id}")
        results = json.loads(response.content)

        self.assertEqual(response.status_code, status.HTTP_200_OK)
        self.assertEqual(results[0]['ride_id'], ride.ride_id)
